SELECT id, NAME as text, pid, pre_fix, is_last, code, level FROM tb_zone WHERE
level = 1
code = #{code};
pid = #{pid}

SELECT * FROM tb_zone WHERE
level = 1
code = #{code};
pid = #{pid}

select CONCAT(
(SELECT name FROM tb_zone where id = #{id})
)

SELECT * FROM tb_zone z WHERE 1=1
AND (z.name like #{keyWord} OR z.code like #{keyWord} OR z.spell_name like #{keyWord} OR z.fir_spell_name like #{keyWord})
AND LENGTH(z.code)=9 AND SUBSTR(code FROM 7 FOR 9)='000' AND SUBSTR(code FROM 5 FOR 9) !='00000' AND NOT EXISTS (SELECT * FROM tb_transport_fee tf WHERE z.code = tf.des_zone_code AND tf.units_id = #{unitId}) LIMIT ${(page-1)*pagesize},${pagesize}
